SELECT au.USERNAME,c.NAME,f.QUESTION,f.ANSWER FROM APP_USER au INNER JOIN STUDY_SET ss ON au.USER_ID = ss.OWNER_ID INNER JOIN STUDY_SET_CARD ssc ON ss.STUDY_SET_ID = ssc.STUDY_SET_ID INNER JOIN FLASHCARD f ON ssc.FLASHCARD_ID = f.FLASHCARD_ID INNER JOIN CATEGORY c ON f.CATEGORY_ID = c.CATEGORY_ID WHERE au.USER_ID = 4;